let Joi,User;_4ef‍.x([["register",()=>register],["login",()=>login],["check",()=>check],["logout",()=>logout],["remove",()=>remove]]);_4ef‍.w("@hapi/joi",[["default",["Joi"],function(v){Joi=v}]]);_4ef‍.w("../../models/user",[["default",["User"],function(v){User=v}]]);


       const register = async (ctx) => {
  const schema = Joi.object().keys({
    username: Joi.string().alphanum().min(3).max(20).required(),
    nickname: Joi.string().min(3).max(20).required(),
    password: Joi.string().min(3).max(20).required(),
  });
  const result = schema.validate(ctx.request.body);
  if (result.error) {
    console.log('error');
    ctx.status = 400;
    ctx.body = result.error;
    return;
  }

  const { username, nickname, password } = ctx.request.body;
  try {
    const exists = await User.findByUsername(username);
    if (exists) {
      ctx.status = 409;
      return;
    }

    const user = new User({
      username,
      nickname,
    });
    await user.setPassword(password);
    await user.save();

    ctx.body = user.serialize();

    const token = user.generateToken();
    ctx.cookies.set('access_token', token, {
      maxAge: 1000 * 60 * 60 * 24 * 7,
      httpOnly: true,
    });
  } catch (e) {
    ctx.throw(500, e);
  }
};

       const login = async (ctx) => {
  const { username, password } = ctx.request.body;
  if (!username || !password) {
    ctx.status = 401;
    return;
  }

  try {
    const user = await User.findByUsername(username);
    if (!user) {
      ctx.status = 401;
      return;
    }

    const valid = await user.checkPassword(password);
    if (!valid) {
      ctx.status = 401;
      return;
    }

    ctx.body = user.serialize();

    const token = user.generateToken();
    ctx.cookies.set('access_token', token, {
      maxAge: 1000 * 60 * 60 * 24 * 7,
      httpOnly: true,
    });
  } catch (e) {
    ctx.throw(500, e);
  }
};

       const check = async (ctx) => {
  const { user } = ctx.state;
  if (!user) {
    ctx.status = 401;
    return;
  }
  ctx.body = user;
};

       const logout = async (ctx) => {
  ctx.cookies.set('access_token');
  ctx.status = 204;
};

       const remove = async (ctx) => {
  const { user } = ctx.state;
  if (!user) {
    ctx.status = 401;
    return;
  }

  try {
    await User.findByIdAndRemove(user._id).exec();
    ctx.status = 204;
  } catch (e) {
    ctx.throw(500, e);
  }
};
